                  <div className="appointment-actions">
                    <button 
                      onClick={() => updateAppointmentStatus(appointment.id, 'confirmed')}
                      className="btn btn-primary"
                      disabled={updatingAppointment === appointment.id}
                    >
                      <svg className="icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                        <path d="M5 12L10 17L20 7"/>
                      </svg>
                      Confirmer
                    </button>
                    <button 
                      onClick={() => updateAppointmentStatus(appointment.id, 'cancelled')}
                      className="btn btn-secondary"
                      disabled={updatingAppointment === appointment.id}
                    >
                      <svg className="icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                        <circle cx="12" cy="12" r="10"/>
                        <line x1="15" y1="9" x2="9" y2="15"/>
                        <line x1="9" y1="9" x2="15" y2="15"/>
                      </svg>
                      Annuler
                    </button>
                  </div>
                )}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  ), [appointments, loadingAppointments, updatingAppointment, fetchAppointments, updateAppointmentStatus, availableSlots, newSlot, handleNewSlotChange, addTimeSlot, deleteTimeSlot]);

  // Render current section
  const renderSection = () => {
    switch (currentSection) {
      case 'gallery':
